def generarMenu(color):
    if color:
        imprimirMenuColor()
    else:
        imprimirMenu()
    entrada = input("╔════════════════════════╗\n║ SELECCIONA UNA OPCIÓN: ║\n╚════════════════════════╝\n")

    try:
        entrada = int(entrada)
        if entrada == 1: #Jugar
            return 1
        elif entrada == 2: #Guardar partida
            return 2
        elif entrada == 3: #Cargar partida
            return 3
        elif entrada == 4: #Salir del juego
            return 4
        else:
            return 0 #Si la entrada no es válida se vuelve a preguntar
    except:
        return 0 #Si la entrada no es válida se vuelve a preguntar